import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-galery',
  templateUrl: './galery.component.html',
  styleUrls: ['./galery.component.css']
})
export class GaleryComponent {

  public title = 'Galeria de imagens';
  @Input() public pictures = ['hm'];

  public currentPic = 0;

  public returnToFirst() {
    this.currentPic = this.first();
  }

  public goToLast() {
    this.currentPic = this.last();
  }


  public nextPicture(){
    if(this.currentPic < this.pictures.length - 1){
      return ++this.currentPic
    }

    return this.currentPic;
  }

  public beforePicture(){
    if(this.currentPic > 0){
      return --this.currentPic;
    }

    return this.currentPic;
  }

  public first(){
    return 0;
  }

  public last(){
    return this.pictures.length -1
  }
}
